import { useState, useEffect } from 'react';
import './App.css';

const API_BASE = 'http://localhost:8000/api';

function App() {
  const [habits, setHabits] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [activeView, setActiveView] = useState('home');
  const [selectedHabit, setSelectedHabit] = useState(null);
  const [noteText, setNoteText] = useState('');
  const [loading, setLoading] = useState(true);

  const [formData, setFormData] = useState({
    name: '',
    frequency: 'daily',
    category: 'health',
    startDate: new Date().toISOString().split('T')[0]
  });

  // Load habits from API
  useEffect(() => {
    fetchHabits();
  }, []);

  // Set document title to app name
  useEffect(() => {
    document.title = 'Habit Hero';
  }, []);

  const fetchHabits = async () => {
    try {
      const response = await fetch(`${API_BASE}/habits/`);
      if (response.ok) {
        const data = await response.json();
        setHabits(data);
      }
    } catch (error) {
      console.error('Error fetching habits:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
  e.preventDefault();

  // Map formData to match API field names
  const dataToSend = {
    name: formData.name,
    frequency: formData.frequency,
    category: formData.category,
    start_date: formData.startDate  // Map startDate to start_date
  };

  try {
    const response = await fetch(`${API_BASE}/habits/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(dataToSend)  // Send the mapped data
    });

    if (response.ok) {
      const newHabit = await response.json();
      setHabits([...habits, newHabit]);
      setFormData({
        name: '',
        frequency: 'daily',
        category: 'health',
        startDate: new Date().toISOString().split('T')[0]
      });
      setShowForm(false);
    } else {
      // Handle error response
      const errorData = await response.json();
      console.error('Error creating habit:', errorData);
      alert('Error creating habit: ' + JSON.stringify(errorData));
    }
  } catch (error) {
    console.error('Error creating habit:', error);
    alert('Network error. Please try again.');
  }
};

  const handleCheckIn = async (habitId, date) => {
    const dateStr = date || new Date().toISOString().split('T')[0];

    try {
      const response = await fetch(`${API_BASE}/habits/${habitId}/checkin/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ date: dateStr })
      });

      if (response.ok) {
        // Refresh habits to get updated data
        await fetchHabits();
      }
    } catch (error) {
      console.error('Error checking in:', error);
    }
  };

  const addNote = async (habitId, note) => {
    if (!note.trim()) return;

    try {
      const response = await fetch(`${API_BASE}/habits/${habitId}/add_note/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text: note })
      });

      if (response.ok) {
        // Refresh habits to get updated data
        await fetchHabits();
      }
    } catch (error) {
      console.error('Error adding note:', error);
    }
  };

  const deleteHabit = async (habitId) => {
    if (window.confirm('Are you sure you want to delete this habit?')) {
      try {
        const response = await fetch(`${API_BASE}/habits/${habitId}/`, {
          method: 'DELETE'
        });

        if (response.ok) {
          const newHabits = habits.filter(habit => habit.id !== habitId);
          setHabits(newHabits);
          setSelectedHabit(null);
          setActiveView('home');
        }
      } catch (error) {
        console.error('Error deleting habit:', error);
      }
    }
  };

  const calculateStreak = (habit) => {
    const checkIns = habit.checkins || [];
    if (checkIns.length === 0) return 0;

    const sortedDates = checkIns
      .map(ci => ci.date)
      .sort((a, b) => new Date(b) - new Date(a));
    let streak = 0;
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    for (let i = 0; i < sortedDates.length; i++) {
      const checkDate = new Date(sortedDates[i]);
      checkDate.setHours(0, 0, 0, 0);
      const expectedDate = new Date(today);
      expectedDate.setDate(today.getDate() - i);

      if (checkDate.getTime() === expectedDate.getTime()) {
        streak++;
      } else {
        break;
      }
    }

    return streak;
  };

  const calculateSuccessRate = (habit) => {
    const startDate = new Date(habit.start_date);
    const today = new Date();
    const daysPassed = Math.floor((today - startDate) / (1000 * 60 * 60 * 24)) + 1;
    const checkIns = habit.checkins || [];

    if (daysPassed <= 0) return 0;
    return Math.round((checkIns.length / daysPassed) * 100);
  };

  const getBestDay = (habit) => {
    const checkIns = habit.checkins || [];
    if (checkIns.length === 0) return 'No data';

    const dayCounts = {};
    checkIns.forEach(ci => {
      const day = new Date(ci.date).toLocaleDateString('en-US', { weekday: 'long' });
      dayCounts[day] = (dayCounts[day] || 0) + 1;
    });

    let maxDay = '';
    let maxCount = 0;
    for (const [day, count] of Object.entries(dayCounts)) {
      if (count > maxCount) {
        maxCount = count;
        maxDay = day;
      }
    }

    return maxDay || 'No data';
  };

  const isCheckedToday = (habit) => {
    const today = new Date().toISOString().split('T')[0];
    return (habit.checkins || []).some(ci => ci.date === today);
  };

