    const icons = {
      health: 'ðŸ’ª',
      work: 'ðŸ’¼',
      learning: 'ðŸ“š',
      fitness: 'ðŸƒ',
      'mental health': 'ðŸ§˜',
      productivity: 'âš¡'
    };
    return icons[category] || 'â­';
  };

